con:config.script:
def exportTrx = ["Sale 02", "Sale 03", "Auth 02", "Auth 03", "Auth 04"]

exportTrx.each
{
	def trxName = it
	def responseParts = context.testCase.getTestStepByName( it ).httpRequest.response.getResponseContent().split( "&" )

	if ( responseParts )
	{
		responseParts.each
		{
			if ( it.contains( "PNREF" ) )
			{
				referenceNumber = it.split( "=" )[1]
				if ( "Sale 02" == trxName ) com.eviware.soapui.SoapUI.globalProperties.setPropertyValue( "PFPTrxSale02", referenceNumber )
				if ( "Sale 03" == trxName ) com.eviware.soapui.SoapUI.globalProperties.setPropertyValue( "PFPTrxSale03", referenceNumber )
				if ( "Auth 02" == trxName ) com.eviware.soapui.SoapUI.globalProperties.setPropertyValue( "PFPTrxAuth02", referenceNumber )
				if ( "Auth 03" == trxName ) com.eviware.soapui.SoapUI.globalProperties.setPropertyValue( "PFPTrxAuth03", referenceNumber )
				if ( "Auth 04" == trxName ) com.eviware.soapui.SoapUI.globalProperties.setPropertyValue( "PFPTrxAuth04", referenceNumber )
			}
		}
	}
}
